public class Aula7{
    
    public static void compeTOP(){
        System.out.println("compeTOP");
    }

    public static int somaINT(int n1, int n2){
        int soma = n1+n2;
        return soma;
    }

    public static void tabuada(int n){
        for(int i=1; i <=10; i++){
            System.out.println(n + "x" + i + "= " + (n*i));
        }
    }
    public static void main(String[] args) {
         
        compeTOP();
        System.out.println(somaINT(8, 8));
        tabuada(7);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
    }
}